mapper.insert:
INSERT INTO TBL_USERS
(
 USER_ID,
 USER_PW,
 USER_NAME,
 USER_EMAIL
 )
VALUES (#{userId},
        #{userPw},
        #{userName},
        #{userEmail}
        );

mapper.update:
UPDATE TBL_USERS
SET USER_ID = #{userId},
    USER_PW    = #{userPw},
    USER_NAME = #{userName},
    USER_EMAIL = #{userEmail}
WHERE USER_ID = #{userId}

UPDATE TBL_USERS
SET  USER_PW    = '0000'
WHERE USER_ID = #{userId}
